const createPageItems = (count: number) => {
 const pageItems: { key: string; label: string; pageNumber: number }[] = [];
 for (let i = 0; i < count; i += 1) {
 pageItems.push({
 key: `${i + 1}-page-of-${count}`,
 label: t("Common:PageOfTotalPage", { page: i + 1, totalPage: count }),
 pageNumber: i,
 });
 }
 return pageItems;
 };
